import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUsers } from '../redux/userSlice';

const Home = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users); // This should be an array

  useEffect(() => {
    dispatch(fetchUsers());
  }, [dispatch]);

  // Log users to debug
  console.log("Users data:", users); // Check what users is at runtime

  return (
    <div>
      <h2>Home</h2>
      <ul>
        {Array.isArray(users) && users.length > 0 ? ( // Check if users is an array
          users.map((user) => (
            <li key={user.id}>
              {user.name} - {user.email}
              {/* Include edit and delete buttons here */}
            </li>
          ))
        ) : (
          <li>No users found</li> // Handle case when there are no users
        )}
      </ul>
    </div>
  );
};

export default Home;
