import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

export const fetchUsers = createAsyncThunk('users/fetchUsers', async () => {
    const response = await fetch('http://localhost:5000/api/users'); // Adjust if needed
    if (!response.ok) {
      throw new Error('Failed to fetch users');
    }
    return response.json(); // This should return an array of users
  });

export const deleteUser = createAsyncThunk('users/deleteUser', async (id) => {
  await fetch(`/api/users/${id}`, { method: 'DELETE' });
  return id;
});

export const loginUser = createAsyncThunk('users/loginUser', async ({ email, password }) => {
    const response = await fetch('http://localhost:5000/api/login', { // Ensure URL is correct
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
  
    if (!response.ok) {
      throw new Error('Login failed'); // This will trigger the rejected case
    }
  
    return response.json(); // Return user data on successful login
  });
  
  const userSlice = createSlice({
    name: 'users',
    initialState: {
        currentUser: null, // This should be an object or null
        users: [], // This should be an array for storing users
        status: null,
      },
    reducers: {},
    extraReducers: (builder) => {
      builder
        .addCase(loginUser.fulfilled, (state, action) => {
          state.currentUser = action.payload;  // Save the user in the state
          state.status = 'loggedIn';
        })
        .addCase(loginUser.rejected, (state) => {
          state.status = 'loginFailed';  // Set status to indicate failure
        })
        .addCase(fetchUsers.fulfilled, (state, action) => {
            return action.payload;
          })
          .addCase(deleteUser.fulfilled, (state, action) => {
            return state.filter(user => user.id !== action.payload);
          })
    },
  });
  

export default userSlice.reducer;
