import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUsers } from '../redux/userSlice';

const Home = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);  // Fetch users from Redux state
  const currentUser = useSelector((state) => state.users.currentUser);  // Get current user

  useEffect(() => {
    dispatch(fetchUsers());
  }, [dispatch]);

  console.log(currentUser);  // Check if currentUser is populated

  return (
    <div>
      <h2>Home</h2>
      <p>Welcome, {currentUser ? currentUser.name : 'Guest'}!</p>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {user.name} - {user.email}
            <button>Edit</button>
            <button onClick={() => dispatch(deleteUser(user.id))}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
